import React, { useState, useEffect, useRef } from "react";
import "./App.css";
import { Plus, Globe, Megaphone, AppWindow, Mic, Send, Book } from "lucide-react";

const API_URL = "http://localhost:5000/webhooks/rest/webhook";

function App() {
  const [conversations, setConversations] = useState([]);
  const [activeConversationId, setActiveConversationId] = useState(null);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [darkMode, setDarkMode] = useState(true);
  const messagesEndRef = useRef(null);
  const textareaRef = useRef(null);

  const generateId = () => `conv_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

  const getCurrentConversation = () => {
    return conversations.find((conv) => conv.id === activeConversationId) || { messages: [] };
  };

  useEffect(() => {
    const saved = localStorage.getItem("chatbot_conversations");
    if (saved) {
      const parsed = JSON.parse(saved);
      if (parsed.length === 0 || !parsed[0].messages.length) {
        const firstConversation = {
          id: generateId(),
          title: "New Chat",
          messages: [],
          createdAt: new Date(),
          updatedAt: new Date(),
        };
        setConversations([firstConversation]);
        setActiveConversationId(firstConversation.id);
        localStorage.setItem("chatbot_conversations", JSON.stringify([firstConversation]));
      } else {
        setConversations(parsed);
        setActiveConversationId(parsed[0].id);
      }
    } else {
      createNewConversation();
    }
  }, []);

  useEffect(() => {
    localStorage.setItem("chatbot_conversations", JSON.stringify(conversations));
  }, [conversations]);

  useEffect(() => {
    const savedTheme = localStorage.getItem("darkMode");
    if (savedTheme !== null) {
      setDarkMode(JSON.parse(savedTheme));
    }
  }, []);

  useEffect(() => {
    document.body.className = darkMode ? "dark-theme" : "light-theme";
  }, [darkMode]);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [conversations, activeConversationId, isTyping]);

  useEffect(() => {
    if (textareaRef.current) {
      textareaRef.current.style.height = "auto";
      textareaRef.current.style.height = Math.min(textareaRef.current.scrollHeight, 200) + "px";
    }
  }, [input]);

  const createNewConversation = () => {
    const id = generateId();
    const newConversation = {
      id,
      title: "New Chat",
      messages: [],
      createdAt: new Date(),
      updatedAt: new Date(),
    };
    setConversations((prev) => [newConversation, ...prev]);
    setActiveConversationId(id);
  };

  const updateConversationTitle = (id, firstMessage) => {
    setConversations((prev) =>
      prev.map((conv) => {
        if (conv.id === id && conv.title === "New Chat") {
          return {
            ...conv,
            title: firstMessage.length > 50 ? firstMessage.substring(0, 50) + "..." : firstMessage,
            updatedAt: new Date(),
          };
        }
        return conv;
      })
    );
  };

  const deleteConversation = (id, e) => {
    e.stopPropagation();
    setConversations((prev) => {
      const filtered = prev.filter((conv) => conv.id !== id);
      if (activeConversationId === id && filtered.length > 0) {
        setActiveConversationId(filtered[0].id);
      } else if (filtered.length === 0) {
        createNewConversation();
      }
      return filtered;
    });
  };

  const switchConversation = (id) => setActiveConversationId(id);

  const toggleTheme = () => {
    setDarkMode((prev) => {
      localStorage.setItem("darkMode", !prev);
      return !prev;
    });
  };

  const sendMessage = async () => {
    if (!input.trim()) return;
    const userMessage = {
      sender: "user",
      text: input.trim(),
      timestamp: new Date(),
    };
    const currentConversation = getCurrentConversation();
    const updatedMessages = [...currentConversation.messages, userMessage];

    setConversations((prev) =>
      prev.map((conv) =>
        conv.id === activeConversationId ? { ...conv, messages: updatedMessages, updatedAt: new Date() } : conv
      )
    );

    if (currentConversation.title === "New Chat") updateConversationTitle(activeConversationId, userMessage.text);
    setInput("");
    setIsTyping(true);
    try {
      const response = await fetch(API_URL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ sender: "user", message: userMessage.text }),
      });
      const data = await response.json();
      const botReplies = data.map((d) => ({
        sender: "bot",
        text: d.text,
        timestamp: new Date(),
      }));
      setTimeout(() => {
        setConversations((prev) =>
          prev.map((conv) =>
            conv.id === activeConversationId ? { ...conv, messages: [...updatedMessages, ...botReplies], updatedAt: new Date() } : conv
          )
        );
        setIsTyping(false);
      }, 1200);
    } catch {
      const errorMessage = {
        sender: "bot",
        text: "I'm having trouble connecting right now. Please try again later.",
        timestamp: new Date(),
      };
      setTimeout(() => {
        setConversations((prev) =>
          prev.map((conv) =>
            conv.id === activeConversationId ? { ...conv, messages: [...updatedMessages, errorMessage], updatedAt: new Date() } : conv
          )
        );
        setIsTyping(false);
      }, 1200);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  const currentConversation = getCurrentConversation();

  return (
    <div className="app">
      {/* Sidebar */}
      <aside className={`sidebar ${sidebarOpen ? "open" : "closed"}`}>
        <div className="sidebar-header">
          <div className="logo-section">
            <div className="logo-icon">⚡</div>
            <span className="logo-text">InfoPilot</span>
          </div>
          <button
            className="sidebar-toggle"
            onClick={() => setSidebarOpen((prev) => !prev)}
            aria-label="Toggle sidebar"
          >
            <svg width={16} height={16} viewBox="0 0 24 24" fill="currentColor">
              <path d="M3 6h18v2H3V6zm0 5h18v2H3v-2zm0 5h18v2H3v-2z" />
            </svg>
          </button>
        </div>

        <div className="sidebar-content">
          <div className="search-bar">
            <svg width={16} height={16} viewBox="0 0 24 24" fill="currentColor">
              <path d="M15.5 14h-.79l-.28-.27A6.471 6.471 0 0016 9.5 6.5 6.5 0 109 16c1.61 0 3.09-.59 4.23-1.57l.27.28v.79l5 4.99L20.49 19l-4.99-5" />
              <circle cx={9} cy={9} r={5} />
            </svg>
            <input type="search" placeholder="Search" aria-label="Search conversations" />
          </div>

          <div className="nav-section">
            <div className={`nav-item ${true ? "active" : ""}`}>
              <svg width={16} height={16} viewBox="0 0 24 24" fill="currentColor">
                <path d="M12 2L15.09 8.26L22 9.27L17 14.14L18.18 20.02L12 17.77L5.82 20.02L7 14.14L2 9.27L8.91 8.26L12 2Z" />
              </svg>
              <span>InfoPilot</span>
            </div>
            <div className="nav-item" onClick={() => window.open("https://your-docs-link.com", "_blank")}>
              <Book size={16} />
              <span>Docs</span>
            </div>
          </div>

          <button className="new-chat-btn" onClick={createNewConversation}>
            <svg width={16} height={16} viewBox="0 0 24 24" fill="currentColor">
              <path d="M19 13H13V19H11V13H5V11H11V5H13V11H19V13Z" />
            </svg>
            <span>New Chat</span>
          </button>

          <div className="conversation-list">
            {conversations.map(({ id, title }) => (
              <div
                key={id}
                className={`conversation-item${id === activeConversationId ? " active" : ""}`}
                tabIndex={0}
                role="button"
                aria-pressed={id === activeConversationId}
                onClick={() => switchConversation(id)}
                onKeyDown={(e) => { if (e.key === "Enter") switchConversation(id); }}
              >
                <span className="title">{title}</span>
                <div className="actions">
                  <button
                    aria-label="Delete conversation"
                    onClick={(e) => {
                      e.stopPropagation();
                      deleteConversation(id);
                    }}
                    className="delete"
                  >
                    ✕
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>

        <div className="sidebar-footer">
          <div className="user-profile" tabIndex={-1} aria-label="User profile">
            <div className="avatar" aria-hidden="true">V</div>
            <span>VJ</span>
          </div>
        </div>
      </aside>

      <main className="main-content">
        {/* Theme toggle top right */}
        <div className="main-header" style={{ display: "flex", justifyContent: "flex-end", padding: "20px 32px 8px 0" }}>
          <button
            className="theme-toggle"
            onClick={toggleTheme}
            aria-label={`Switch to ${darkMode ? "light" : "dark"} mode`}
            title={`Switch to ${darkMode ? "light" : "dark"} mode`}
          >
            {darkMode ? (
              <svg width={20} height={20} viewBox="0 0 24 24" fill="currentColor" aria-hidden="true">
                <circle cx="12" cy="12" r="5" />
                <path d="M12 2v2M12 20v2M4.22 4.22l1.42 1.42M16.95 16.95l1.42 1.42M2 12h2M20 12h2M4.22 19.78l1.42-1.42M16.95 7.05l1.42-1.42" />
              </svg>
            ) : (
              <svg width={20} height={20} viewBox="0 0 24 24" fill="currentColor" aria-hidden="true">
                <path d="M21 12a9 9 0 11-9-9c.39 0 .77.04 1.14.1A7 7 0 0021 12z" />
              </svg>
            )}
          </button>
        </div>

        <div className="chat-area">
          <div className="messages-container">
            {(!currentConversation.messages || currentConversation.messages.length === 0) && (
              <div className="chat-empty" role="region" aria-live="polite">
                <h1>InfoPilot</h1>
                <p>Your AI assistant powered by advanced language understanding.</p>
                <p>Ask anything and I'll help you!</p>
              </div>
            )}

            {currentConversation.messages.map(({ sender, text }, index) => (
              <div key={index} className={`message-group ${sender}`} tabIndex={-1} aria-label={`${sender === "user" ? "You" : "Bot"} said`}>
                <div className="message-content">{text}</div>
              </div>
            ))}

            {isTyping && (
              <div className="typing-indicator" aria-live="polite" aria-label="Bot is typing">
                <span className="dot"></span><span className="dot"></span><span className="dot"></span>
              </div>
            )}

            <div ref={messagesEndRef} />
          </div>
        </div>

        <form className="input-area" onSubmit={(e) => { e.preventDefault(); sendMessage(); }} aria-label="Chat input form">
          <textarea
            ref={textareaRef}
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={handleKeyPress}
            placeholder="Ask anything"
            rows={1}
            aria-label="Message input"
            className="message-input"
          />
          <div className="input-actions">
            <button type="button" className="icon-btn" title="Add"> <Plus /> </button>
            <button type="button" className="icon-btn" title="Language"> <Globe /> </button>
            <button type="button" className="icon-btn" title="Announcements"> <Megaphone /> </button>
            <button type="button" className="icon-btn" title="Apps"> <AppWindow /> </button>
            <button type="button" className="icon-btn" title="Voice"> <Mic /> </button>
            <button type="submit" className="send-btn" disabled={!input.trim()} title="Send message"> <Send /> </button>
          </div>
        </form>
      </main>
    </div>
  );
}

export default App;
