import React, { useState, useEffect, useRef } from "react";
import "./App.css";

const API_URL = "http://localhost:5005/webhooks/rest/webhook";

// Sample chat history data
const SAMPLE_CHAT_HISTORY = [
  {
    id: 1,
    title: "Renewable Energy Basics",
    preview: "What are the main types of renewable energy?",
    time: "2 hours ago",
    messages: [
      { sender: "user", text: "What are the main types of renewable energy?" },
      { sender: "bot", text: "The main types of renewable energy include solar, wind, hydroelectric, geothermal, and biomass energy. Each has unique advantages and applications." }
    ]
  },
  {
    id: 2,
    title: "Solar Panel Installation",
    preview: "How much does solar panel installation cost?",
    time: "1 day ago",
    messages: [
      { sender: "user", text: "How much does solar panel installation cost?" },
      { sender: "bot", text: "Solar panel installation costs vary by location and system size, typically ranging from $15,000 to $25,000 before incentives." }
    ]
  },
  {
    id: 3,
    title: "Wind Energy Efficiency",
    preview: "How efficient are modern wind turbines?",
    time: "3 days ago",
    messages: [
      { sender: "user", text: "How efficient are modern wind turbines?" },
      { sender: "bot", text: "Modern wind turbines can achieve efficiency rates of 35-45%, with some advanced models reaching up to 50% efficiency." }
    ]
  }
];

// Sample suggestion prompts
const SUGGESTIONS = [
  {
    title: "Solar Energy Basics",
    desc: "Learn about solar panels, installation, and benefits"
  },
  {
    title: "Wind Power Systems",
    desc: "Understand wind turbines and their applications"
  },
  {
    title: "Energy Conservation",
    desc: "Tips for reducing energy consumption at home"
  },
  {
    title: "Renewable vs Fossil",
    desc: "Compare renewable and traditional energy sources"
  }
];

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const [currentChatId, setCurrentChatId] = useState(null);
  const [chatHistory, setChatHistory] = useState(SAMPLE_CHAT_HISTORY);
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const messagesEndRef = useRef(null);
  const inputRef = useRef(null);

  // Auto-scroll to bottom
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages, isTyping]);

  // Auto-resize input
  useEffect(() => {
    if (inputRef.current) {
      inputRef.current.style.height = 'auto';
      inputRef.current.style.height = Math.min(inputRef.current.scrollHeight, 120) + 'px';
    }
  }, [input]);

  const startNewChat = () => {
    setMessages([]);
    setCurrentChatId(null);
    setInput("");

    // Add welcome message
    setTimeout(() => {
      setMessages([{
        sender: "bot",
        text: "Hi! I'm your Renewable Energy Awareness Assistant. Ask me anything about solar, wind, or energy conservation!",
        time: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
      }]);
    }, 500);
  };

  const loadChatHistory = (chatId) => {
    const chat = chatHistory.find(c => c.id === chatId);
    if (chat) {
      setCurrentChatId(chatId);
      setMessages(chat.messages.map(msg => ({
        ...msg,
        time: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
      })));
    }
    setSidebarOpen(false);
  };

  const sendMessage = async () => {
    if (!input.trim()) return;

    const newMessage = {
      sender: "user",
      text: input.trim(),
      time: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
    };

    const newMessages = [...messages, newMessage];
    setMessages(newMessages);
    setInput("");
    setIsTyping(true);

    try {
      const res = await fetch(API_URL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ sender: "user", message: input }),
      });

      const data = await res.json();
      const botReplies = data.map((d) => ({
        sender: "bot",
        text: d.text,
        time: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
      }));

      setTimeout(() => {
        setMessages([...newMessages, ...botReplies]);

        // Update or create chat history entry
        if (currentChatId) {
          setChatHistory(prev => prev.map(chat => 
            chat.id === currentChatId 
              ? { ...chat, messages: [...newMessages, ...botReplies] }
              : chat
          ));
        } else {
          const newChatId = Date.now();
          const newChat = {
            id: newChatId,
            title: input.slice(0, 30) + (input.length > 30 ? '...' : ''),
            preview: input,
            time: "Just now",
            messages: [...newMessages, ...botReplies]
          };
          setChatHistory(prev => [newChat, ...prev]);
          setCurrentChatId(newChatId);
        }
      }, 800);
    } catch (err) {
      setTimeout(() => {
        const errorMessage = {
          sender: "bot",
          text: "I apologize, but I'm experiencing connection difficulties. Please check your network and try again.",
          time: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
        };
        setMessages([...newMessages, errorMessage]);
      }, 800);
    } finally {
      setTimeout(() => {
        setIsTyping(false);
      }, 600);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  const handleSuggestionClick = (suggestion) => {
    setInput(suggestion.desc);
    inputRef.current?.focus();
  };

  // Initialize with welcome
  useEffect(() => {
    startNewChat();
  }, []);

  return (
    <div className="copilot-app">
      {/* Sidebar - Chat History */}
      <div className={`copilot-sidebar ${sidebarOpen ? 'open' : ''}`}>
        <div className="sidebar-header">
          <div className="sidebar-title">Chat History</div>
          <button className="new-chat-button" onClick={startNewChat}>
            New Chat
          </button>
        </div>

        <div className="chat-history">
          {chatHistory.map((chat) => (
            <div
              key={chat.id}
              className={`chat-history-item ${currentChatId === chat.id ? 'active' : ''}`}
              onClick={() => loadChatHistory(chat.id)}
            >
              <div className="chat-title">{chat.title}</div>
              <div className="chat-preview">{chat.preview}</div>
              <div className="chat-time">{chat.time}</div>
            </div>
          ))}
        </div>
      </div>

      {/* Main Content */}
      <div className="copilot-main">
        {/* Header */}
        <div className="copilot-header">
          <button
            className="mobile-menu-button"
            onClick={() => setSidebarOpen(!sidebarOpen)}
          >
            â˜°
          </button>
          <div className="copilot-logo">AI</div>
          <div className="copilot-header-title">Renewable Energy Assistant</div>
          <div className="copilot-header-actions">
            <button className="header-button">Settings</button>
            <button className="header-button">Help</button>
          </div>
        </div>

        {/* Chat Window */}
        <div className="copilot-chat-window">
          {messages.length === 0 ? (
            <div className="copilot-welcome">
              <div className="welcome-logo">ðŸŒ±</div>
              <h1 className="welcome-title">Welcome to Your Energy Assistant</h1>
              <p className="welcome-subtitle">
                I'm here to help you learn about renewable energy, sustainability, and energy conservation. 
                Ask me anything or try one of these suggestions:
              </p>
              <div className="welcome-suggestions">
                {SUGGESTIONS.map((suggestion, index) => (
                  <div
                    key={index}
                    className="suggestion-card"
                    onClick={() => handleSuggestionClick(suggestion)}
                  >
                    <div className="suggestion-title">{suggestion.title}</div>
                    <div className="suggestion-desc">{suggestion.desc}</div>
                  </div>
                ))}
              </div>
            </div>
          ) : (
            <>
              {messages.map((message, index) => (
                <div key={index} className="copilot-message">
                  <div className={`message-avatar ${message.sender}`}>
                    {message.sender === 'user' ? 'You' : 'AI'}
                  </div>
                  <div className="message-content">
                    <div className="message-header">
                      <div className="message-sender">
                        {message.sender === 'user' ? 'You' : 'AI Assistant'}
                      </div>
                      <div className="message-time">{message.time}</div>
                    </div>
                    <div className={`message-text ${message.sender}`}>
                      {message.text}
                    </div>
                  </div>
                </div>
              ))}

              {isTyping && (
                <div className="copilot-typing">
                  <div className="typing-avatar">AI</div>
                  <div className="typing-content">
                    <span>AI is thinking</span>
                    <div className="typing-dots">
                      <div className="typing-dot"></div>
                      <div className="typing-dot"></div>
                      <div className="typing-dot"></div>
                    </div>
                  </div>
                </div>
              )}

              <div ref={messagesEndRef} />
            </>
          )}
        </div>

        {/* Input Area */}
        <div className="copilot-input-area">
          <div className="input-container">
            <div className="input-wrapper">
              <textarea
                ref={inputRef}
                className="message-input"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder="Ask me about renewable energy..."
                disabled={isTyping}
                rows={1}
              />
              <button
                className="send-button"
                onClick={sendMessage}
                disabled={isTyping || !input.trim()}
              />
            </div>
            <div className="input-footer">
              AI can make mistakes. Consider checking important information.
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
