import React, { useState, useEffect, useRef } from "react";
import "./App.css";

const API_URL = "http://localhost:5005/webhooks/rest/webhook";

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const messagesEndRef = useRef(null);

  // Smooth auto-scroll
  useEffect(() => {
    const timeout = setTimeout(() => {
      messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }, 100);
    return () => clearTimeout(timeout);
  }, [messages]);

  // Initial greeting
  useEffect(() => {
    const fetchGreeting = () => {
      setTimeout(() => {
        setMessages([
          {
            sender: "bot",
            text: "Hello! InfoPilot at your service. What can I do for you today?",
          },
        ]);
      }, 1000);
    };
    fetchGreeting();
  }, []);

  const sendMessage = async () => {
    if (!input.trim()) return;
    const userMessage = { sender: "user", text: input.trim() };
    const newMessages = [...messages, userMessage];
    setMessages(newMessages);
    setInput("");

    setTimeout(() => setIsTyping(true), 100);

    try {
      const res = await fetch(API_URL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ sender: "user", message: userMessage.text }),
      });
      const data = await res.json();
      const botReplies = data.map(d => ({ sender: "bot", text: d.text }));
      setTimeout(() => {
        setMessages([...newMessages, ...botReplies]);
        setIsTyping(false);
      }, 800);
    } catch (err) {
      setTimeout(() => {
        setMessages([
          ...newMessages,
          {
            sender: "bot",
            text: "I apologize, but I'm experiencing connection difficulties. Please check your network and try again.",
          },
        ]);
        setIsTyping(false);
      }, 800);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  return (
    <div className="app-container">
      <div className="chat-container">
        <h1 className="title">InfoPilot Chatbot</h1>

        <div className="chat-window">
          {messages.map((msg, idx) => (
            <div
              key={idx}
              className={`chat-bubble ${msg.sender} animate-in`}
            >
              {msg.text}
            </div>
          ))}

          {isTyping && (
            <div className="chat-bubble typing animate-in">
              <span className="dot"></span>
              <span className="dot"></span>
              <span className="dot"></span>
            </div>
          )}
          <div ref={messagesEndRef} />
        </div>

        <form
          className="chat-input-area"
          onSubmit={(e) => {
            e.preventDefault();
            sendMessage();
          }}
        >
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={handleKeyPress}
            placeholder="Type a message..."
            aria-label="Message input"
          />
          <button type="submit" disabled={!input.trim()}>
            Send
          </button>
        </form>
      </div>
    </div>
  );
}

export default App;
