import React, { useState, useEffect, useRef } from "react";
import "./App.css";

const API_URL = "http://localhost:5005/webhooks/rest/webhook";

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const [particles, setParticles] = useState([]);
  const [buttonState, setButtonState] = useState('idle'); // idle, sending, sent
  const [isMessageAnimating, setIsMessageAnimating] = useState(false);
  const messagesEndRef = useRef(null);

  // Auto-scroll to the bottom when messages update
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  // Particle system generator
  useEffect(() => {
    const generateParticles = () => {
      const newParticles = Array.from({ length: 20 }, (_, i) => ({
        id: i,
        left: Math.random() * 100,
        delay: Math.random() * 8,
        duration: 8 + Math.random() * 4,
      }));
      setParticles(newParticles);
    };

    generateParticles();
    const interval = setInterval(generateParticles, 12000);
    return () => clearInterval(interval);
  }, []);

  // Initial greeting when component mounts
  useEffect(() => {
    const fetchGreeting = async () => {
      setIsTyping(true);
      try {
        const res = await fetch(API_URL, {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ sender: "user", message: "hello" }),
        });
        const data = await res.json();
        const botReplies = data.map((d) => ({
          sender: "bot",
          text: d.text,
        }));
        setMessages([
          {
            sender: "bot",
            text: "Hi! I'm your Renewable Energy Awareness Assistant. Ask me anything about solar, wind, or energy conservation!",
          },
          ...botReplies,
        ]);
      } catch (err) {
        setMessages([{ sender: "bot", text: "Error connecting to server." }]);
      } finally {
        setIsTyping(false);
      }
    };

    fetchGreeting();
  }, []);

  // Enhanced send message with animations
  const sendMessage = async () => {
    if (!input.trim()) return;

    setButtonState('sending');
    setIsMessageAnimating(true);

    const newMessages = [...messages, { sender: "user", text: input }];
    setMessages(newMessages);
    setInput("");
    setIsTyping(true);

    try {
      const res = await fetch(API_URL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ sender: "user", message: input }),
      });
      const data = await res.json();
      const botReplies = data.map((d) => ({
        sender: "bot",
        text: d.text,
      }));
      setMessages([...newMessages, ...botReplies]);
      setButtonState('sent');
      setTimeout(() => setButtonState('idle'), 1000);
    } catch (err) {
      setMessages([
        ...newMessages,
        { sender: "bot", text: "Error connecting to server." },
      ]);
      setButtonState('idle');
    } finally {
      setIsTyping(false);
      setIsMessageAnimating(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  return (
    <div className="chat-container">
      {/* Particle System */}
      <div className="particles-container">
        {particles.map(particle => (
          <div
            key={particle.id}
            className="particle"
            style={{
              left: `${particle.left}%`,
              animationDelay: `${particle.delay}s`,
              animationDuration: `${particle.duration}s`
            }}
          />
        ))}
      </div>

      <h1 className="title">AI Energy Assistant</h1>

      <div className="chat-window focus-glow-container">
        {messages.map((msg, index) => (
          <div key={index} className={`chat-bubble ${msg.sender}`}>
            {msg.text}
            <div className="reaction-bar">
              <span className="reaction-emoji">üëç</span>
              <span className="reaction-emoji">‚ù§Ô∏è</span>
              <span className="reaction-emoji">üòä</span>
            </div>
          </div>
        ))}

        {isTyping && (
          <div className="chat-bubble typing">
            <div className="typing-container">
              <div className="typing-avatar"></div>
              <div className="dot-container">
                <span className="dot"></span>
                <span className="dot"></span>
                <span className="dot"></span>
              </div>
            </div>
          </div>
        )}

        <div ref={messagesEndRef} />
      </div>

      <div className="chat-input-area focus-glow-container">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="Type your message..."
          disabled={buttonState === 'sending'}
        />
        <button 
          onClick={sendMessage} 
          className={buttonState}
          disabled={buttonState === 'sending' || !input.trim()}
        >
          <span className="button-text">Send</span>
          <div className="loading-spinner"></div>
        </button>
      </div>
    </div>
  );
}

export default App;
