import React, { useState, useEffect, useRef } from "react";
import "./App.css";

const API_URL = "http://localhost:5005/webhooks/rest/webhook";

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const [isInputFocused, setIsInputFocused] = useState(false);
  const messagesEndRef = useRef(null);

  // Smooth auto-scroll
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ 
      behavior: "smooth",
      block: "end"
    });
  }, [messages]);

  // Enhanced initial greeting with smooth timing
  useEffect(() => {
    const fetchGreeting = async () => {
      setTimeout(() => {
        setMessages([
          {
            sender: "bot",
            text: "Hi! I'm your Renewable Energy Awareness Assistant. Ask me anything about solar, wind, or energy conservation!",
          }
        ]);
      }, 1000);
    };

    fetchGreeting();
  }, []);

  // Smooth message sending with enhanced timing
  const sendMessage = async () => {
    if (!input.trim()) return;

    const newMessages = [...messages, { sender: "user", text: input }];
    setMessages(newMessages);
    setInput("");
    setIsTyping(true);

    try {
      const res = await fetch(API_URL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ sender: "user", message: input }),
      });
      const data = await res.json();
      const botReplies = data.map((d) => ({
        sender: "bot",
        text: d.text,
      }));

      // Enhanced timing for smoother UX
      setTimeout(() => {
        setMessages([...newMessages, ...botReplies]);
      }, 600);
    } catch (err) {
      setTimeout(() => {
        setMessages([
          ...newMessages,
          { sender: "bot", text: "Error connecting to server. Please try again." },
        ]);
      }, 600);
    } finally {
      setTimeout(() => {
        setIsTyping(false);
      }, 400);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  const handleInputFocus = () => setIsInputFocused(true);
  const handleInputBlur = () => setIsInputFocused(false);

  return (
    <div className="chat-container">
      <h1 className="title">AI Energy Assistant</h1>

      <div className="chat-window">
        {messages.map((msg, index) => (
          <div 
            key={index} 
            className={`chat-bubble ${msg.sender}`}
            style={{ 
              animationDelay: `${index * 0.1}s`,
              opacity: 0,
              animation: `messageSlideIn 0.6s ease-out ${index * 0.1}s forwards`
            }}
          >
            {msg.text}
          </div>
        ))}

        {isTyping && (
          <div className="chat-bubble typing">
            <span style={{ fontSize: '0.9rem', color: '#64748b' }}>AI is thinking</span>
            <div style={{display: 'flex', gap: '2px'}}>
              <span className="dot"></span>
              <span className="dot"></span>
              <span className="dot"></span>
            </div>
          </div>
        )}

        <div ref={messagesEndRef} />
      </div>

      <div className={`chat-input-area ${isInputFocused ? 'focused' : ''}`}>
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={handleKeyPress}
          onFocus={handleInputFocus}
          onBlur={handleInputBlur}
          placeholder="Type your message here..."
          disabled={isTyping}
          className={isTyping ? 'loading' : ''}
        />
        <button 
          onClick={sendMessage} 
          disabled={isTyping || !input.trim()}
          className={isTyping ? 'loading' : ''}
        >
          {isTyping ? 'Sending...' : 'Send'}
        </button>
      </div>
    </div>
  );
}

export default App;
