import {useState,useEffect,useRef} from 'react';
import './App.css';

const API='http://localhost:5005/webhooks/rest/webhook';

export default function App(){
  const [msgs,setMsgs]=useState([]);
  const [txt,setTxt]=useState('');
  const [typing,setTyping]=useState(false);
  const [btn,setBtn]=useState('idle');          // idle | sending
  const endRef=useRef(null);

  /* scroll to last */
  useEffect(()=>{endRef.current?.scrollIntoView({behavior:'smooth'});},[msgs]);

  /* greet on mount */
  useEffect(()=>{(async()=>{
      setTyping(true);
      const r=await fetch(API,{method:'POST',headers:{'Content-Type':'application/json'},
        body:JSON.stringify({sender:'user',message:'hello'})});
      const d=await r.json();
      setMsgs([{sender:'bot',text:"Hi! Ask me anything â€¦"},...d.map(x=>({sender:'bot',text:x.text}))]);
      setTyping(false);
  })();},[]);

  /* send message */
  const send=async()=>{
    if(!txt.trim())return;
    setBtn('sending');
    const mine={sender:'user',text:txt};
    setMsgs(m=>[...m,mine]);
    setTxt('');setTyping(true);
    try{
      const r=await fetch(API,{method:'POST',headers:{'Content-Type':'application/json'},
        body:JSON.stringify({sender:'user',message:mine.text})});
      const d=await r.json();
      setMsgs(m=>[...m,...d.map(x=>({sender:'bot',text:x.text}))]);
    }catch{setMsgs(m=>[...m,{sender:'bot',text:'(server error)'}]);}
    finally{setBtn('idle');setTyping(false);}
  };

  return(
  <div className="chat-container">
    <h1 className="title">Enterprise AI Assistant</h1>

    <div className="chat-window">
      {msgs.map((m,i)=>(
        <div key={i} className={`chat-bubble ${m.sender}`}>{m.text}</div>
      ))}
      {typing&&(
        <div className="chat-bubble typing">
          <div className="typing-avatar">ðŸ¤–</div>
          <div className="dot-wrap">
            <span className="dot"></span><span className="dot"></span><span className="dot"></span>
          </div>
        </div>
      )}
      <div ref={endRef}/>
    </div>

    <div className="chat-input-area">
      <input
        value={txt}
        onChange={e=>setTxt(e.target.value)}
        onKeyDown={e=>e.key==='Enter'&&send()}
        placeholder="Type your messageâ€¦"
        disabled={btn==='sending'}
      />
      <button className={btn} onClick={send} disabled={btn==='sending'}>
        <span className="btn-txt">Send</span>
        <div className="spinner"/>
      </button>
    </div>
  </div>);
}
