import React, { useState, useEffect, useRef } from "react";
import "./App.css";

const API_URL = "http://localhost:5005/webhooks/rest/webhook";

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const messagesEndRef = useRef(null);

  // Auto-scroll to the bottom when messages update
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages, isTyping]);

  // Initial greeting when component mounts - NO THINKING INDICATOR
  useEffect(() => {
    const fetchGreeting = async () => {
      // Immediate welcome message without thinking
      setTimeout(() => {
        setMessages([
          {
            sender: "bot",
            text: "Hi! I'm your Renewable Energy Awareness Assistant. Ask me anything about solar, wind, or energy conservation!",
          }
        ]);
      }, 800); // Brief delay for smooth entrance
    };

    fetchGreeting();
  }, []);

  const sendMessage = async () => {
    if (!input.trim()) return;

    // Add user message immediately
    const userMessage = { sender: "user", text: input.trim() };
    const newMessages = [...messages, userMessage];
    setMessages(newMessages);

    // Clear input and start thinking AFTER user message is sent
    setInput("");

    // Small delay before showing thinking indicator for smoother UX
    setTimeout(() => {
      setIsTyping(true);
    }, 200);

    try {
      const res = await fetch(API_URL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ sender: "user", message: userMessage.text }),
      });

      const data = await res.json();
      const botReplies = data.map((d) => ({
        sender: "bot",
        text: d.text,
      }));

      // Simulate realistic thinking time (minimum 800ms)
      const thinkingTime = Math.max(800, Math.min(2000, userMessage.text.length * 50));

      setTimeout(() => {
        setIsTyping(false);
        setMessages([...newMessages, ...botReplies]);
      }, thinkingTime);

    } catch (err) {
      // Handle error with thinking delay
      setTimeout(() => {
        setIsTyping(false);
        setMessages([
          ...newMessages,
          { 
            sender: "bot", 
            text: "I'm having trouble connecting right now. Please check your connection and try again." 
          },
        ]);
      }, 1000);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  const handleInputChange = (e) => {
    setInput(e.target.value);
  };

  return (
    <div className="chat-container">
      <h1 className="title">AI Energy Assistant</h1>

      <div className="chat-window">
        {messages.map((msg, index) => (
          <div 
            key={index} 
            className={`chat-bubble ${msg.sender}`}
            style={{ 
              animationDelay: `${index * 0.1}s`,
            }}
          >
            {msg.text}
          </div>
        ))}

        {isTyping && (
          <div className="chat-bubble typing">
            <span style={{ 
              fontSize: '0.9rem', 
              color: '#64748b',
              marginRight: '0.5rem'
            }}>
              AI is thinking
            </span>
            <div style={{display: 'flex', gap: '2px'}}>
              <span className="dot"></span>
              <span className="dot"></span>
              <span className="dot"></span>
            </div>
          </div>
        )}

        <div ref={messagesEndRef} />
      </div>

      <div className="chat-input-area">
        <input
          type="text"
          value={input}
          onChange={handleInputChange}
          onKeyPress={handleKeyPress}
          placeholder="Ask me about renewable energy..."
          disabled={isTyping}
          className={isTyping ? 'loading' : ''}
        />
        <button 
          onClick={sendMessage} 
          disabled={isTyping || !input.trim()}
          className={isTyping ? 'loading' : ''}
        >
          {isTyping ? 'Sending...' : 'Send'}
        </button>
      </div>
    </div>
  );
}

export default App;
