import React, { useState, useEffect, useRef } from "react";
import "./App.css";
import { Plus, Globe, Megaphone, AppWindow, Mic, Send, Book } from "lucide-react";
import InfoPilotLogo from './assets/infopilot.svg';

const API_URL = "http://localhost:5005/webhook/rest";

function App() {
  const [conversations, setConversations] = useState([]);
  const [activeConversationId, setActiveConversationId] = useState(null);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [darkMode, setDarkMode] = useState(true);
  const messagesEndRef = useRef(null);
  const textareaRef = useRef(null);

  const generateId = () => `conv_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

  const getCurrentConversation = () => {
    return conversations.find(conv => conv.id === activeConversationId) || { messages: [] };
  };

  useEffect(() => {
    const savedConversations = localStorage.getItem('chatbot_conversations');
    if (savedConversations) {
      const parsed = JSON.parse(savedConversations);
      if (parsed.length === 0 || !parsed[0].messages || parsed[0].messages.length === 0) {
        const defaultConversation = {
          id: generateId(),
          title: "New Chat",
          messages: [],
          createdAt: new Date(),
          updatedAt: new Date(),
        };
        setConversations([defaultConversation]);
        setActiveConversationId(defaultConversation.id);
        localStorage.setItem('chatbot_conversations', JSON.stringify([defaultConversation]));
      } else {
        setConversations(parsed);
        setActiveConversationId(parsed[0].id);
      }
    } else {
      createNewConversation();
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('chatbot_conversations', JSON.stringify(conversations));
  }, [conversations]);

  useEffect(() => {
    const savedTheme = localStorage.getItem('darkMode');
    if (savedTheme !== null) {
      setDarkMode(JSON.parse(savedTheme));
    }
  }, []);

  useEffect(() => {
    document.body.className = darkMode ? 'dark-theme' : 'light-theme';
  }, [darkMode]);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [conversations, activeConversationId, isTyping]);

  useEffect(() => {
    if (textareaRef.current) {
      textareaRef.current.style.height = "auto";
      textareaRef.current.style.height = Math.min(textareaRef.current.scrollHeight, 200) + "px";
    }
  }, [input]);

  const createNewConversation = () => {
    const newId = generateId();
    const newConversation = {
      id: newId,
      title: "New Chat",
      messages: [],
      createdAt: new Date(),
      updatedAt: new Date(),
    };
    setConversations(prev => [newConversation, ...prev]);
    setActiveConversationId(newId);
  };

  const updateConversationTitle = (conversationId, firstMessage) => {
    setConversations(prev =>
      prev.map(conv => {
        if (conv.id === conversationId && conv.title === "New Chat") {
          return {
            ...conv,
            title: firstMessage.length > 50 ? firstMessage.substring(0, 50) + "..." : firstMessage,
            updatedAt: new Date(),
          };
        }
        return conv;
      })
    );
  };

  const deleteConversation = id => {
    setConversations(prev => {
      const filtered = prev.filter(c => c.id !== id);
      if (activeConversationId === id && filtered.length > 0) {
        setActiveConversationId(filtered[0].id);
      }
      return filtered;
    });
  };

  const switchConversation = id => setActiveConversationId(id);

  const toggleTheme = () => {
    setDarkMode(prev => {
      localStorage.setItem('darkMode', !prev);
      return !prev;
    });
  };

  const sendMessage = async () => {
    if (!input.trim()) return;
    const userMessage = { sender: "user", text: input.trim(), timestamp: new Date() };
    const currentConv = getCurrentConversation();
    const updated = [...currentConv.messages, userMessage];
    setConversations(prev =>
      prev.map(conv => (conv.id === activeConversationId ? { ...conv, messages: updated, updatedAt: new Date() } : conv))
    );
    if (currentConv.title === "New Chat") updateConversationTitle(activeConversationId, userMessage.text);
    setInput("");
    setIsTyping(true);
    try {
      const res = await fetch(API_URL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ sender: "user", message: userMessage.text }),
      });
      const data = await res.json();
      const botReplies = data.map(d => ({ sender: "bot", text: d.text, timestamp: new Date() }));
      setTimeout(() => {
        setConversations(prev =>
          prev.map(conv =>
            conv.id === activeConversationId ? { ...conv, messages: [...updated, ...botReplies], updatedAt: new Date() } : conv
          )
        );
        setIsTyping(false);
      }, 1200);
    } catch {
      const errorMsg = { sender: "bot", text: "Problem connecting, please try again.", timestamp: new Date() };
      setTimeout(() => {
        setConversations(prev =>
          prev.map(conv =>
            conv.id === activeConversationId ? { ...conv, messages: [...updated, errorMsg], updatedAt: new Date() } : conv
          )
        );
        setIsTyping(false);
      }, 1200);
    }
  };

  const handleKeyPress = e => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  const currentConversation = getCurrentConversation();

  return (
    <div className="app">
      <aside className={`sidebar ${sidebarOpen ? "open" : "closed"}`}>
        <div className="sidebar-header">
          <div className="sidebar-logo">
            <div className="logo-icon">⚡</div>
            <span className="logo-text">InfoPilot</span>
          </div>
          <button className="sidebar-toggle" onClick={() => setSidebarOpen(prev => !prev)}>
            <svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor">
              <path d="M3 6h18v2H3V6zm0 5h18v2H3v-2zm0 5h18v2H3v-2z" />
            </svg>
          </button>
        </div>

        <div className="sidebar-content">
          <div className="search-bar">
            <svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor">
              <path d="M15.5 14h-.79l-.28-.27A6.471 6.471 0 0 0 16 9.5 6.5 6.5 0 0 0 9.5 16c1.61 0 3.09-.59 4.23-1.57l.27.28v.79l5 4.99L20.49 19l-4.99-5zM9 14c-1.99 0-3.61-1.61-3.61-3.61S7.01 6.78 9 6.78s3.61 1.61 3.61 3.61S10.99 14 9 14z" />
            </svg>
            <input type="text" placeholder="Search" />
          </div>
          <div className="nav-section">
            <div className="nav-item active">
              <svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor">
                <path d="M12 2l3.09 6.26 6.91 1-5 4.87 1.18 6.88-6.18-3.53-6.18 3.53 1.18-6.88-5-4.87 6.91-1z" />
              </svg>
              <span>InfoPilot</span>
            </div>
            <div
              className="nav-item"
              onClick={() => window.open("https://www.notion.so/InfoPilot-Documentation-260b8d998280?source=copy", "_blank")}
              role="button"
              tabIndex={0}
              onKeyPress={e => {
                if (e.key === "Enter") {
                  window.open("https://www.notion.so/InfoPilot-Documentation-260b8d998280?source=copy", "_blank");
                }
              }}
            >
              <Book size={16} />
              <span>Docs</span>
            </div>
          </div>
          <button className="new-chat-btn" onClick={createNewConversation}>
            <svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor">
              <path d="M19 13h-6v6H9v-6H5v-2h4V5h2v6h6v2z" />
            </svg>
            <span>New Chat</span>
          </button>

          <div className="conversation-history">
            {conversations.map(c => (
              <div key={c.id} className={`conversation-item ${activeConversationId === c.id ? "active" : ""}`} onClick={() => switchConversation(c.id)} role="button" tabIndex={0}>
                <span>{c.title}</span>
                <button onClick={e => { e.stopPropagation(); deleteConversation(c.id); }} className="delete-btn" aria-label="Delete conversation">
                  ✕
                </button>
              </div>
            ))}
          </div>
        </div>

        <div className="sidebar-footer">
          <div className="theme-toggle-container">
            <button className="theme-toggle" onClick={toggleTheme} title={`Switch to ${darkMode ? "light" : "dark"} mode`}>
              {darkMode ? (
                <svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor">
                  <path d="..." />
                </svg>
              ) : (
                <svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor">
                  <path d="..." />
                </svg>
              )}
              <span className="theme-label">{darkMode ? "Light" : "Dark"} mode</span>
            </button>
          </div>

          <div className="user-profile">
            <div className="user-avatar">V</div>
            <span>VJ</span>
          </div>
        </div>
      </aside>

      <main className={`main-content ${sidebarOpen ? "sidebar-open" : "sidebar-closed"}`}>
        <header className="main-header">
          <div className="header-title">
            <h1>
              InfoPilot
              <img src={InfoPilotLogo} alt="Logo" className="header-logo" />
            </h1>
          </div>

          <div className="header-actions">
            <button className="action-btn theme-toggle-header" onClick={toggleTheme} title={`Switch to ${darkMode ? "light" : "dark"} mode`}>
              {darkMode ? (
                <svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor">
                  <path d="..." />
                </svg>
              ) : (
                <svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor">
                  <path d="..." />
                </svg>
              )}
            </button>
            <button className="action-btn" aria-label="Clear chat">
              <svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor">
                <path d="..." />
              </svg>
            </button>
          </div>
        </header>

        <div className="chat-area">
          <div className="messages-container">
            {!currentConversation.messages || currentConversation.messages.length === 0 ? (
              <div className="chat-title-section" role="presentation">
                <h1 className="chat-title">Welcome to InfoPilot</h1>
                <p className="chat-subtitle">Your AI assistant powered by advanced language understanding. Ask anything you like!</p>
              </div>
            ) : (
              currentConversation.messages.map((msg, idx) => (
                <div key={idx} className={`message-group ${msg.sender}`}>
                  <div className="message-content">
                    <div className="message-text">{msg.text}</div>
                  </div>
                </div>
              ))
            )}

            {isTyping && (
              <div className="message-group bot">
                <div className="typing-indicator">
                  <span className="typing-dot"></span>
                  <span className="typing-dot"></span>
                  <span className="typing-dot"></span>
                </div>
              </div>
            )}
            <div ref={messagesEndRef} />
          </div>
        </div>

        {/* Input Section */}
        <div className="input-area">
          <div className="input-container">
            <div className="input-wrapper">
              <div className="input-icons">
                <button className="input-icon-btn" title="Add">
                  <Plus size={20} />
                </button>
                <button className="input-icon-btn" title="Language">
                  <Globe size={20} />
                </button>
                <button className="input-icon-btn" title="Announcements">
                  <Megaphone size={20} />
                </button>
                <button className="input-icon-btn" title="Apps">
                  <AppWindow size={20} />
                </button>
              </div>
              <textarea
                ref={textareaRef}
                value={input}
                onChange={e => setInput(e.target.value)}
                onKeyDown={handleKeyPress}
                placeholder="Ask anything"
                className="message-input"
                rows="1"
              />
              <div className="input-right-icons">
                <button className="input-icon-btn" title="Voice input">
                  <Mic size={20} />
                </button>
                <button
                  className="send-btn"
                  onClick={sendMessage}
                  disabled={!input.trim()}
                  title="Send message"
                >
                  <Send size={20} />
                </button>
              </div>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
