import React, { useState, useEffect, useRef } from "react";
import "./App.css";

const API_URL = "http://localhost:5005/webhooks/rest/webhook";

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const [buttonState, setButtonState] = useState('idle'); // idle, sending, sent
  const messagesEndRef = useRef(null);

  // Auto-scroll with smooth animation
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ 
      behavior: "smooth",
      block: "end"
    });
  }, [messages]);

  // Initial greeting with enhanced animation
  useEffect(() => {
    const fetchGreeting = async () => {
      // Add delay for dramatic effect
      setTimeout(() => {
        setMessages([
          {
            sender: "bot",
            text: "üöÄ Welcome to the Ultra-Professional AI Assistant! I'm here to help with renewable energy solutions, sustainability advice, and environmental insights. How can I assist you today?",
          }
        ]);
      }, 1000);
    };

    fetchGreeting();
  }, []);

  // Enhanced send message with premium animations
  const sendMessage = async () => {
    if (!input.trim()) return;

    setButtonState('sending');
    const newMessages = [...messages, { sender: "user", text: input }];
    setMessages(newMessages);
    setInput("");
    setIsTyping(true);

    try {
      const res = await fetch(API_URL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ sender: "user", message: input }),
      });
      const data = await res.json();
      const botReplies = data.map((d) => ({
        sender: "bot",
        text: d.text,
      }));

      // Add slight delay for better UX
      setTimeout(() => {
        setMessages([...newMessages, ...botReplies]);
        setButtonState('sent');
        setTimeout(() => setButtonState('idle'), 1000);
      }, 800);
    } catch (err) {
      setTimeout(() => {
        setMessages([
          ...newMessages,
          { sender: "bot", text: "‚ö†Ô∏è Connection error. Please check your server and try again." },
        ]);
        setButtonState('idle');
      }, 800);
    } finally {
      setTimeout(() => {
        setIsTyping(false);
      }, 500);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  return (
    <div className="chat-container">
      <h1 className="title">ü§ñ AI Assistant Pro</h1>

      <div className="chat-window">
        {messages.map((msg, index) => (
          <div key={index} className={`chat-bubble ${msg.sender}`}>
            {msg.text}
          </div>
        ))}

        {isTyping && (
          <div className="chat-bubble typing">
            <div className="typing-avatar">ü§ñ</div>
            <div className="dot-container">
              <span className="dot"></span>
              <span className="dot"></span>
              <span className="dot"></span>
            </div>
          </div>
        )}

        <div ref={messagesEndRef} />
      </div>

      <div className="chat-input-area">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="‚ú® Type your message here..."
          disabled={buttonState === 'sending'}
          className={buttonState === 'sending' ? 'loading' : ''}
        />
        <button 
          onClick={sendMessage} 
          disabled={buttonState === 'sending' || !input.trim()}
          className={buttonState}
        >
          {buttonState === 'sending' ? '‚è≥' : buttonState === 'sent' ? '‚úÖ' : 'üöÄ'}
          <span style={{ marginLeft: '8px' }}>
            {buttonState === 'sending' ? 'Sending' : buttonState === 'sent' ? 'Sent' : 'Send'}
          </span>
        </button>
      </div>
    </div>
  );
}

export default App;
