import React, { useState, useEffect, useRef } from "react";
import "./App.css";
import { Plus, Globe, Megaphone, AppWindow, Mic, Send, Book, Star, StarFilled } from "lucide-react";

const API_URL = "http://localhost:5005/webhook/rest";

function App() {
  const [conversations, setConversations] = useState([]);
  const [activeConversationId, setActiveConversationId] = useState(null);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [darkMode, setDarkMode] = useState(true);
  const messagesEndRef = useRef(null);
  const textareaRef = useRef(null);

  const generateId = () => `conv_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

  const getCurrentConversation = () => {
    return conversations.find((conv) => conv.id === activeConversationId) || { messages: [] };
  };

  useEffect(() => {
    const saved = localStorage.getItem("chatbot_conversations");
    if (saved) {
      const parsed = JSON.parse(saved);
      if (!parsed.length || !parsed[0].messages.length) {
        const first = {
          id: generateId(),
          title: "New Chat",
          messages: [],
          favorite: false,
          createdAt: new Date(),
          updatedAt: new Date(),
        };
        setConversations([first]);
        setActiveConversationId(first.id);
        localStorage.setItem("chatbot_conversations", JSON.stringify([first]));
      } else {
        setConversations(parsed);
        setActiveConversationId(parsed[0].id);
      }
    } else {
      createNewConversation();
    }
  }, []);

  useEffect(() => {
    localStorage.setItem("chatbot_conversations", JSON.stringify(conversations));
  }, [conversations]);

  useEffect(() => {
    const dark = localStorage.getItem("darkMode");
    if (dark !== null) setDarkMode(JSON.parse(dark));
  }, []);

  useEffect(() => {
    document.body.className = darkMode ? "dark-theme" : "light-theme";
  }, [darkMode]);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [conversations, activeConversationId, isTyping]);

  useEffect(() => {
    if (textareaRef.current) {
      textareaRef.current.style.height = "auto";
      textareaRef.current.style.height = Math.min(textareaRef.current.scrollHeight, 200) + "px";
    }
  }, [input]);

  const createNewConversation = () => {
    const id = generateId();
    const newConv = {
      id,
      title: "New Chat",
      messages: [],
      favorite: false,
      createdAt: new Date(),
      updatedAt: new Date(),
    };
    setConversations((prev) => [newConv, ...prev]);
    setActiveConversationId(id);
  };

  const updateConversationTitle = (id, firstMsg) => {
    setConversations((prev) =>
      prev.map((conv) =>
        conv.id === id && conv.title === "New Chat"
          ? { ...conv, title: firstMsg.length > 50 ? firstMsg.slice(0, 50) + "..." : firstMsg, updatedAt: new Date() }
          : conv
      )
    );
  };

  const deleteConversation = (id) => {
    setConversations((prev) => {
      const filtered = prev.filter((conv) => conv.id !== id);
      if (activeConversationId === id && filtered.length) setActiveConversationId(filtered[0].id);
      return filtered;
    });
  };

  const toggleFavorite = (id) => {
    setConversations((prev) =>
      prev.map((conv) => (conv.id === id ? { ...conv, favorite: !conv.favorite } : conv))
    );
  };

  const switchConversation = (id) => setActiveConversationId(id);

  const toggleTheme = () => {
    setDarkMode((prev) => {
      localStorage.setItem("darkMode", !prev);
      return !prev;
    });
  };

  const sendMessage = async () => {
    if (!input.trim()) return;
    const userMsg = { sender: "user", text: input.trim(), timestamp: new Date() };
    const currentConv = getCurrentConversation();
    const updatedMsgs = [...currentConv.messages, userMsg];

    setConversations((prev) =>
      prev.map((conv) =>
        conv.id === activeConversationId ? { ...conv, messages: updatedMsgs, updatedAt: new Date() } : conv
      )
    );

    if (currentConv.title === "New Chat") updateConversationTitle(activeConversationId, userMsg.text);

    setInput("");
    setIsTyping(true);

    try {
      const res = await fetch(API_URL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ sender: "user", message: userMsg.text }),
      });
      const data = await res.json();
      const botReplies = data.map((d) => ({ sender: "bot", text: d.text, timestamp: new Date() }));

      setTimeout(() => {
        setConversations((prev) =>
          prev.map((conv) =>
            conv.id === activeConversationId
              ? { ...conv, messages: [...updatedMsgs, ...botReplies], updatedAt: new Date() }
              : conv
          )
        );
        setIsTyping(false);
      }, 1200);
    } catch {
      const errMsg = { sender: "bot", text: "Connection issue, please try again.", timestamp: new Date() };
      setTimeout(() => {
        setConversations((prev) =>
          prev.map((conv) =>
            conv.id === activeConversationId
              ? { ...conv, messages: [...updatedMsgs, errMsg], updatedAt: new Date() }
              : conv
          )
        );
        setIsTyping(false);
      }, 1200);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  const currentConversation = getCurrentConversation();

  // Separate favorite and regular chats
  const favoriteChats = conversations.filter((c) => c.favorite);
  const otherChats = conversations.filter((c) => !c.favorite);

  return (
    <div className="app">
      {/* Sidebar */}
      <aside className={`sidebar ${sidebarOpen ? "open" : "closed"}`}>
        <div className="sidebar-header">
          <div className="sidebar-logo">
            <div className="logo-icon">⚡</div>
            <span className="logo-text">InfoPilot</span>
          </div>
          <button className="sidebar-toggle" onClick={() => setSidebarOpen((prev) => !prev)}>
            <svg width={16} height={16} viewBox="0 0 24 24" fill="currentColor">
              <path d="M3 6h18v2H3V6zm0 5h18v2H3v-2zm0 5h18v2H3v-2z" />
            </svg>
          </button>
        </div>

        <div className="sidebar-content">
          <div className="search-bar">
            <svg width={16} height={16} viewBox="0 0 24 24" fill="currentColor">
              <path d="M15.5 14h-.79l-.28-.27A6.471 6.471 0 0016 9.5 6.5 6.5 0 109 16c1.61 0 3.09-.59 4.23-1.57l.27.28v.79l5 4.99L20.49 19l-4.99-5zM9 14c-1.99 0-3.61-1.61-3.61-3.61S7.01 8.78 9 8.78s3.61 1.61 3.61 3.61S10.99 14 9 14z" />
            </svg>
            <input type="text" placeholder="Search" />
          </div>

          <div className="sidebar-favorites">
            <h4>⭐ Favorites</h4>
            {favoriteChats.length === 0 && <p className="empty-text">No favorites yet</p>}
            {favoriteChats.map(({ id, title }) => (
              <div key={id} className={`conversation-item favorite ${activeConversationId === id ? "active" : ""}`}>
                <span onClick={() => switchConversation(id)} tabIndex={0} role="button">{title}</span>
                <div className="actions">
                  <button
                    onClick={(e) => {
                      e.stopPropagation();
                      toggleFavorite(id);
                    }}
                    aria-label="Unfavorite chat"
                    title="Unfavorite"
                  >
                    <StarFilled color="gold" size={16} />
                  </button>
                  <button
                    onClick={(e) => {
                      e.stopPropagation();
                      deleteConversation(id);
                    }}
                    aria-label="Delete chat"
                    title="Delete"
                  >
                    ✕
                  </button>
                </div>
              </div>
            ))}
          </div>

          <div className="sidebar-chats">
            <h4>Chats</h4>
            {otherChats.length === 0 && <p className="empty-text">No other chats</p>}
            {otherChats.map(({ id, title }) => (
              <div key={id} className={`conversation-item ${activeConversationId === id ? "active" : ""}`}>
                <span onClick={() => switchConversation(id)} tabIndex={0} role="button">{title}</span>
                <div className="actions">
                  <button
                    onClick={(e) => {
                      e.stopPropagation();
                      toggleFavorite(id);
                    }}
                    aria-label="Favorite chat"
                    title="Favorite"
                  >
                    <Star size={16} />
                  </button>
                  <button
                    onClick={(e) => {
                      e.stopPropagation();
                      deleteConversation(id);
                    }}
                    aria-label="Delete chat"
                    title="Delete"
                  >
                    ✕
                  </button>
                </div>
              </div>
            ))}
          </div>

          <button className="new-chat-btn" onClick={createNewConversation}>
            <svg width={16} height={16} viewBox="0 0 24 24" fill="currentColor">
              <path d="M19 13h-6v6H9v-6H5v-2h4V7h6v6z" />
            </svg>
            <span>New Chat</span>
          </button>
        </div>

        <div className="sidebar-footer">
          <button className="theme-toggle" onClick={toggleTheme} aria-label="Toggle theme">
            {darkMode ? <Sun /> : <Moon />}
            <span className="theme-label">{darkMode ? "Light" : "Dark"} mode</span>
          </button>
          <div className="user-profile">
            <div className="user-avatar">V</div>
            <span>VJ</span>
          </div>
        </div>
      </aside>

      <main className={`main-content ${sidebarOpen ? "open" : "closed"}`}>
        <div className="chat-area">
          <div className="messages-container">
            {!currentConversation.messages.length ? (
              <div className="chat-empty">
                <h1>InfoPilot</h1>
                <p>Your AI assistant powered by advanced language understanding. Ask anything.</p>
              </div>
            ) : (
              currentConversation.messages.map(({ sender, text }, idx) => (
                <div key={idx} className={`message-group ${sender}`}>
                  <div className="message-content">{text}</div>
                </div>
              ))
            )}
            {isTyping && (
              <div className="typing-indicator">
                <span className="dot"></span>
                <span className="dot"></span>
                <span className="dot"></span>
              </div>
            )}
            <div ref={messagesEndRef} />
          </div>
        </div>

        <form className="input-area" onSubmit={(e) => { e.preventDefault(); sendMessage(); }}>
          <textarea
            ref={textareaRef}
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={handleKeyPress}
            placeholder="Ask anything"
            rows={1}
            className="message-input"
          />
          <div className="input-actions">
            <button type="button" className="icon-btn" title="Add"><Plus /></button>
            <button type="button" className="icon-btn" title="Language"><Globe /></button>
            <button type="button" className="icon-btn" title="Announcements"><Megaphone /></button>
            <button type="button" className="icon-btn" title="Apps"><AppWindow /></button>
            <button type="button" className="icon-btn" title="Voice"><Mic /></button>
            <button type="submit" className="send-btn" disabled={!input.trim()} title="Send"><Send /></button>
          </div>
        </form>
      </main>
    </div>
  );
}

const Sun = () => (
  <svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor" aria-hidden="true">
    <circle cx="12" cy="12" r="5" />
    <path d="M12 1v2M12 21v2M20.07 3.93l-1.41 1.41M5.34 18.66l-1.41 1.41M23 12h-2M3 12H1M20.07 20.07l-1.41-1.41M5.34 5.34L3.93 3.93" />
  </svg>
);

const Moon = () => (
  <svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor" aria-hidden="true">
    <path d="M21 12.79A9 9 0 0111.21 3a7 7 0 109.79 9.79z" />
  </svg>
);

export default App;
